mapper.select:
SELECT o.*, washpersonname FROM
(SELECT orderid,
washpersonpid,
ordernumber ,
username ,
userphone ,
ordertime ,
washtime ,
cabinetcode,
usercarnumber ,
usercarbrandname ,
usercarcolor ,
usercarlocation ,
chestsite ,
carservicename,
carsereviceprice,
shopaddress,
orderstatus,
orderevaluation
FROM orders, usercar, customer,  carservice, shop
WHERE orders.usercarid = usercar.usercarid
AND customer.userid = orders.userid
AND carservice.carserviceid = orders.carserviceid
GROUP BY orderid DESC) as o LEFT JOIN carwashperson ON o.washpersonpid = carwashperson.washpersonpid

     and orderid = #{orderid}
      and ordernumber = #{ordernumber}
      and ordertime = #{ordertime}
      and washtime = #{washtime}
      and cabinetcode = #{cabinetcode}
      and chestsite = #{chestsite}
      and orderstatus = #{orderstatus}
      and usercarid = #{usercarid}
      and shopid = #{shopid}
      and userid = #{userid}
      and washpersonpid = #{washpersonpid}
      and carserviceid = #{carserviceid}

mapper.update:
update orders
ordernumber = #{ordernumber}, ordertime = #{ordertime}, washtime = #{washtime}, cabinetcode = #{cabinetcode}, chestsite = #{chestsite}, orderstatus = #{orderstatus}, usercarid = #{usercarid}, shopid = #{shopid}, userid = #{userid}, washpersonpid = #{washpersonpid}, carserviceid = #{carserviceid},
where orderid = #{orderid}
